REGRESSION_COEFFICIENT_QUERY = """
SELECT
  coeficiente_regressao.id_power_plant,
  posto_rdh,
  id_power_plant_base,
  tipo_coeficiente,
  mes,
  valor
FROM river.coeficiente_regressao
  JOIN river.power_plant on coeficiente_regressao.id_power_plant = power_plant.id_power_plant
WHERE coeficiente_regressao.end_date IS NULL
"""


PRODUCTIVITY_QUERY = """
SELECT
  produtibilidade.id_power_plant,
  power_plant.posto_rdh,
  produtibilidade.valor
FROM river.produtibilidade
  JOIN river.power_plant on produtibilidade.id_power_plant = power_plant.id_power_plant
WHERE produtibilidade.end_date IS NULL
"""

SUBSYSTEMS_QUERY = """
SELECT id_subsistema, sigla FROM river.subsistema
"""
