class EntityNotFound(Exception):
    def __init__(self, searched_value: str):
        self._searched_value = searched_value

    def __str__(self):
        return f'Entidade não encontrada. Parâmetro da busca: "{self._searched_value}"'


class NoRemviesRoundWithOutputForDate(Exception):
    def __init__(self, base_date: str):
        self._base_date = base_date

    def __str__(self):
        return f'Rodada de remoção de viés com ouptut não encontrada na data: "{self._base_date}"'


class SemanaOperativaDoNotExist(Exception):
    def __init__(self, year: int, month: int, num_rev: int) -> None:
        self._year = year
        self.month = month
        self._num_rev = num_rev

    def __str__(self):
        return f'Não existe rev de número {self._num_rev} no mês {self._year}-{self.month:02d}'


class FileNotFound(Exception):
    def __init__(self, description: str) -> None:
        self._description = description

    def __str__(self):
        return f'Arquivo {self._description}'
