from enum import Enum, unique


@unique
class MessageType(Enum):
    START = ('START', 200)
    END = ('END', 200)
    INFO = ('INFO', 100)
    PROCESSING = ('PROCESSING', 102)
    WARNING = ('WARNING', 199)
    SUCCESS = ('SUCCESS', 200)
    ABORTED = ('ABORTED', 400)
    ERROR = ('ERROR', 500)

    def __init__(self, type_msg, status_code):
        self._type_msg = type_msg
        self._status_code = status_code

    @property
    def type(self):
        return self._type_msg

    @property
    def status_code(self):
        return self._status_code

    @staticmethod
    def list():
        return [s for s in MessageType]
