from chuva_vazao_utils.entities.river.river import River


def test_create_river():
    params = {
        'name': 'name_mock',
        'order': 1,
        'configs': '{"mocked": "config"}',
        'id': 1
    }

    result = River(**params)

    assert result.id == 1
    assert result.name == 'name_mock'
    assert result.order == 1
    assert result.configs == {'mocked': 'config'}


def test_create_river_with_no_config():
    params = {
        'name': 'name_mock',
        'order': 1,
        'configs': None,
        'id': 1
    }

    result = River(**params)

    assert result.id == 1
    assert result.name == 'name_mock'
    assert result.order == 1
    assert result.configs == {}
