from datetime import datetime

from chuva_vazao_utils.entities.utils.data import TimeValue, TimeSeries


def test_time_series():
    # Given
    value_1 = TimeValue(datetime(2022, 12, 5), 10.0)
    value_2 = TimeValue(datetime(2022, 12, 6), 15.0)
    series = TimeSeries([value_1, value_2])
    # When/Then
    assert series.time_list == [datetime(2022, 12, 5), datetime(2022, 12, 6)]
    # When/Then
    assert series.values_list == [10.0, 15.0]
    # When/Then
    assert series.mean_values() == 12.5
