import pytest
from chuva_vazao_utils.entities.utils.abs_dataclass import AbstractDataclass


def test_instantiation():
    with pytest.raises(TypeError):
        AbstractDataclass()


def test_subclass_instantiation():
    # Given
    class TestClass(AbstractDataclass):
        pass
    # When/Then
    with pytest.raises(TypeError):
        TestClass()

    # Given
    class TestOneClass(TestClass, AbstractDataclass):
        pass
    # When/Then
    with pytest.raises(TypeError):
        TestOneClass()

    # Given
    class TestTwoClass(TestClass):
        pass
    # When/Then (no exception raised)
    TestTwoClass()
